#!/usr/bin/env python3

# Count A & T, C & G and print the content of each pair

dna = ''

at = dna.count('A') + dna.count('T')

gc = dna.count('C') + dna.count('G')
dna_len = len(dna)

at_cont = "{:.2%}".format(at/dna_len)
gc_cont = "{:.2%}".format(gc/dna_len)
print('AT content of this DNA is ' + at_cont)
print('GC content of this DNA is ' + gc_cont)
